/**
 * Parses a string and returns metadata about its content.
 */
export function parseStringMetadata(str: string): {
  length: number;
  words: number;
  hasNumbers: boolean;
  hasSpecialChars: boolean;
} {
  if (!str) {
    return {
      length: 0,
      words: 0,
      hasNumbers: false,
      hasSpecialChars: false,
    };
  }

  const words = str.split(/\s+/).filter((word) => {
    if (word.length > 0) {
      return true;
    }
    return false;
  });

  return {
    length: str.length,
    words: words.length,
    hasNumbers: /\d/.test(str),
    hasSpecialChars: /[!@#$%^&*(),.?":{}|<>]/.test(str),
  };
}

